text:
/* 基础字体设置 */
body {
  font-family: var(思源黑体 CN Light,--font-family, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif);
  font-size: var(--font-size, 16px);
  line-height: 1.6;
}

/* 条目框架 */
.tc-tiddler-frame {
  background-color: var(--bg-color, #ffffff);
  color: var(--text-color, #333333);
  padding: 20px;
  margin-bottom: 30px;
  border-radius: 15px;
}

/* 条目标题 */
.tc-title {
  font-size: 1.8em;
  font-weight: 400;
  color: var(--light-trait-100, #333333);
  margin-bottom: 0.5em;
  line-height: 1.3;
}

.tc-subtitle {
  font-size: 1em;
  color: var(--secondary-text-color, #666666);
  margin-bottom: 1em;
}

.tc-tiddler-body {
  font-size: 1em;
  line-height: 1.6;
}

.tc-tiddler-body p {
  margin-bottom: 1em;
}

.tc-tiddler-body h1 {
  font-size: 1.8em;
  font-weight: 700;
  margin-top: 1.5em;
  margin-bottom: 0.5em;
  color: var(--light-trait-200, #444444);
}

.tc-tiddler-body h2 {
  font-size: 1.5em;
  font-weight: 600;
  margin-top: 1.3em;
  margin-bottom: 0.5em;
  color: var(--h2-color, #34495e);
}

.tc-tiddler-body h3 {
  font-size: 1.3em;
  font-weight: 600;
  margin-top: 1.2em;
  margin-bottom: 0.5em;
  color: var(--h3-color, #3c5166);
}

a.tc-tiddlylink {
  color: var(--url-text-color, #0000ff);
  text-decoration: none;
}

a.tc-tiddlylink:hover {
  opacity: 0.8;
}

/* 代码块 */
.tc-code-block {
  font-family: "Inconsolata","思源黑体 CN Light";
  font-size: 2.5em;
  background-color: var(--code-fence-bg-color, #f4f4f4);
  color: var(--code-fence-text-color, #333333);
  padding: 1em;
  border-radius: 4px;
  overflow-x: auto;
}

/* 列表 */
.tc-tiddler-body ul, .tc-tiddler-body ol {
  padding-left: 1.5em;
  margin-bottom: 1em;
}

/* 引用块 */
blockquote {
  border-left: 4px solid var(--blockquote-accent-color, #cccccc);
  padding-left: 1em;
  margin-left: 0;
  color: var(--blockquote-text-color, #666666);
  font-style: italic;
}
